import * as React from 'react';

import { cn } from '@/lib/utils';

import { LfBubble } from './lf-bubble';
import { LfCarousel } from './lf-carousel';
import type { FlexMessage, FlexAction } from './utils/lf-types';

export interface LfMessageProps extends FlexMessage {
  className?: string;
  onAction?: (action: FlexAction) => void;
}

/**
 * LfMessage - Root wrapper component for LINE Flex Messages
 *
 * This component validates and renders LINE Flex Message JSON objects.
 * It accepts a message with type 'flex' and renders either a bubble or carousel
 * based on the contents.
 *
 * @throws {Error} If the message type is not 'flex'
 *
 * @example
 * ```tsx
 * const message = {
 *   type: 'flex',
 *   altText: 'This is a flex message',
 *   contents: {
 *     type: 'bubble',
 *     body: {
 *       type: 'box',
 *       layout: 'vertical',
 *       contents: [
 *         {
 *           type: 'text',
 *           text: 'Hello, World!'
 *         }
 *       ]
 *     }
 *   }
 * };
 *
 * <LfMessage {...message} />
 * ```
 */
const LfMessage = React.forwardRef<HTMLDivElement, LfMessageProps>(
  ({ type, altText, contents, onAction, className }, ref) => {
    // Validate that the message type is 'flex'
    if (type !== 'flex') {
      throw new Error(
        `Invalid message type: expected 'flex', but received '${type}'. ` +
          `LfMessage only accepts LINE Flex Message format with type 'flex'.`,
      );
    }

    // Render based on contents type
    if (contents.type === 'bubble') {
      return (
        <div ref={ref} className={cn('lf-message-wrapper', className)} role="article" aria-label={altText}>
          <LfBubble {...contents} onAction={onAction} />
        </div>
      );
    }

    if (contents.type === 'carousel') {
      return (
        <div ref={ref} className={cn('lf-message-wrapper', className)} role="article" aria-label={altText}>
          <LfCarousel {...contents} onAction={onAction} />
        </div>
      );
    }

    // This should never happen with proper TypeScript types, but adding for runtime safety
    throw new Error(
      `Invalid contents type: expected 'bubble' or 'carousel', but received '${(contents as { type?: unknown }).type}'.`,
    );
  },
);

LfMessage.displayName = 'LfMessage';

export { LfMessage };

import * as React from 'react';

import { cn } from '@/lib/utils';

// Import component renderers
import { LfButton } from './lf-button';
import { LfFiller } from './lf-filler';
import { LfIcon } from './lf-icon';
import { LfImage } from './lf-image';
import { LfSeparator } from './lf-separator';
import { LfSpacer } from './lf-spacer';
import { LfText } from './lf-text';
import { LfVideo } from './lf-video';
import {
  getMarginClass,
  getMarginStyle,
  getOffsetStyles,
  getPaddingStyles,
  getFlexClass,
  getFlexStyle,
  getCornerRadiusClass,
  getCornerRadiusStyle,
  getBorderWidthClass,
  getBorderWidthStyle,
  getBackgroundGradientStyle,
  handleAction,
  getChildSpacingClass,
} from './utils/lf-helpers';
import type { FlexBox, FlexComponent, FlexAction } from './utils/lf-types';
import { layoutVariants, positionVariants, justifyContentVariants, alignItemsVariants } from './utils/lf-variants';

export type LfBoxProps = FlexBox & {
  className?: string;
  onAction?: (action: FlexAction) => void;
};

// Component renderer function
export function renderLfFlexComponent(
  component: FlexComponent,
  index: number,
  layout?: 'horizontal' | 'vertical' | 'baseline',
  onAction?: (action: FlexAction) => void,
  extraClassName?: string,
): React.ReactNode {
  const key = `${component.type}-${index}`;

  switch (component.type) {
    case 'box':
      return <LfBox key={key} {...component} onAction={onAction} className={extraClassName} />;
    case 'button':
      return <LfButton key={key} {...component} onAction={onAction} className={extraClassName} />;
    case 'filler':
      return <LfFiller key={key} {...component} className={extraClassName} />;
    case 'icon':
      return <LfIcon key={key} {...component} className={extraClassName} />;
    case 'image':
      return <LfImage key={key} {...component} onAction={onAction} className={extraClassName} />;
    case 'separator':
      return <LfSeparator key={key} {...component} layout={layout} className={extraClassName} />;
    case 'spacer':
      return <LfSpacer key={key} {...component} className={extraClassName} />;
    case 'text':
      return <LfText key={key} {...component} layout={layout} onAction={onAction} className={extraClassName} />;
    case 'video':
      return <LfVideo key={key} {...component} className={extraClassName} />;
    default:
      return null;
  }
}

const LfBox = React.forwardRef<HTMLDivElement, LfBoxProps>(
  (
    {
      layout = 'vertical',
      contents,
      flex,
      spacing,
      margin,
      paddingAll,
      paddingTop,
      paddingBottom,
      paddingStart,
      paddingEnd,
      position,
      offsetTop,
      offsetBottom,
      offsetStart,
      offsetEnd,
      backgroundColor,
      borderColor,
      borderWidth,
      cornerRadius,
      width,
      maxWidth,
      height,
      maxHeight,
      justifyContent,
      alignItems,
      background,
      action,
      onAction,
      className,
    },
    ref,
  ) => {
    const marginClass = getMarginClass(margin);
    const marginStyle = getMarginStyle(margin);
    const offsetStyles = getOffsetStyles(offsetTop, offsetBottom, offsetStart, offsetEnd);
    const paddingStyles = getPaddingStyles(paddingAll, paddingTop, paddingBottom, paddingStart, paddingEnd);
    const flexClass = getFlexClass(flex);
    const flexStyle = getFlexStyle(flex);
    const cornerRadiusClass = getCornerRadiusClass(cornerRadius);
    const cornerRadiusStyle = getCornerRadiusStyle(cornerRadius);
    const borderWidthClass = getBorderWidthClass(borderWidth);
    const borderWidthStyle = getBorderWidthStyle(borderWidth);
    const backgroundGradientStyle = getBackgroundGradientStyle(background);

    const containerStyle: React.CSSProperties = {
      ...marginStyle,
      ...offsetStyles,
      ...paddingStyles,
      ...flexStyle,
      ...cornerRadiusStyle,
      ...borderWidthStyle,
      ...backgroundGradientStyle,
      ...(backgroundColor && { backgroundColor }),
      ...(borderColor && { borderColor }),
      ...(width && { width, maxWidth: width }),
      ...(maxWidth && { maxWidth }),
      ...(height && { height }),
      ...(maxHeight && { maxHeight }),
    };

    const clickHandler = handleAction(action, onAction);

    return (
      <div
        ref={ref}
        className={cn(
          'lf-box relative max-w-full min-w-0 overflow-hidden',
          layoutVariants({ layout }),
          // Default to flex-1 when flex is not specified (matching .MdBx behavior)
          flex === undefined ? 'flex-1' : flexClass,
          marginClass,
          cornerRadiusClass,
          borderWidthClass,
          borderWidth && 'border-solid',
          position && positionVariants({ position }),
          justifyContent && justifyContentVariants({ justifyContent }),
          alignItems && alignItemsVariants({ alignItems }),
          action && 'cursor-pointer',
          className,
        )}
        style={containerStyle}
        onClick={clickHandler}
      >
        {contents.map((component: FlexComponent, index: number) =>
          renderLfFlexComponent(
            component,
            index,
            layout,
            onAction,
            getChildSpacingClass(spacing, layout, index, component.type),
          ),
        )}
      </div>
    );
  },
);

LfBox.displayName = 'LfBox';

export { LfBox };

import * as React from 'react';

import { cn } from '@/lib/utils';

import { LfBox, renderLfFlexComponent } from './lf-box';
import { handleAction } from './utils/lf-helpers';
import type { FlexBubble, FlexAction } from './utils/lf-types';
import { bubbleSizeVariants } from './utils/lf-variants';

export interface LfBubbleProps extends FlexBubble {
  className?: string;
  onAction?: (action: FlexAction) => void;
}

const LfBubble = React.forwardRef<HTMLDivElement, LfBubbleProps>(
  ({ size = 'mega', direction = 'ltr', header, hero, body, footer, styles, action, onAction, className }, ref) => {
    const clickHandler = handleAction(action, onAction);

    const directionClass = direction === 'rtl' ? 'dir-rtl' : 'dir-ltr';

    return (
      <div
        ref={ref}
        className={cn(bubbleSizeVariants({ size }), directionClass, action && 'cursor-pointer', className)}
        onClick={action && !onAction ? clickHandler : undefined}
        dir={direction}
      >
        {/* Header */}
        {header && (
          <div
            className="flex flex-none flex-col"
            style={styles?.header?.backgroundColor ? { backgroundColor: styles.header.backgroundColor } : undefined}
          >
            <LfBox
              {...header}
              onAction={onAction}
              {...(header.paddingAll === undefined &&
                header.paddingTop === undefined &&
                header.paddingBottom === undefined &&
                header.paddingStart === undefined &&
                header.paddingEnd === undefined && {
                  paddingAll:
                    size === 'mega' || size === 'giga'
                      ? '20px'
                      : size === 'kilo'
                        ? '13px'
                        : size === 'nano' || size === 'micro'
                          ? '10px'
                          : '11px',
                })}
            />
          </div>
        )}

        {/* Hero */}
        {hero && (
          <div
            className="flex-none"
            style={styles?.hero?.backgroundColor ? { backgroundColor: styles.hero.backgroundColor } : undefined}
          >
            {hero.type === 'box' ? (
              <LfBox {...hero} onAction={onAction} />
            ) : (
              renderLfFlexComponent(hero, 0, undefined, onAction)
            )}
          </div>
        )}

        {/* Body */}
        {body && (
          <div
            className="flex flex-1 flex-col"
            style={styles?.body?.backgroundColor ? { backgroundColor: styles.body.backgroundColor } : undefined}
          >
            <LfBox
              {...body}
              onAction={onAction}
              {...(body.paddingAll === undefined &&
                body.paddingTop === undefined &&
                body.paddingBottom === undefined &&
                body.paddingStart === undefined &&
                body.paddingEnd === undefined && {
                  paddingAll:
                    size === 'mega' || size === 'giga'
                      ? '20px'
                      : size === 'kilo'
                        ? '13px'
                        : size === 'nano' || size === 'micro'
                          ? '10px'
                          : '11px',
                  paddingTop: size === 'mega' || size === 'giga' ? '19px' : undefined,
                  paddingBottom: footer
                    ? size === 'kilo' || size === 'hecto' || size === 'deca'
                      ? '17px'
                      : '10px'
                    : undefined,
                })}
            />
          </div>
        )}

        {/* Footer */}
        {footer && (
          <div
            className="flex flex-none flex-col"
            style={styles?.footer?.backgroundColor ? { backgroundColor: styles.footer.backgroundColor } : undefined}
          >
            <LfBox
              {...footer}
              onAction={onAction}
              {...(footer.paddingAll === undefined &&
                footer.paddingTop === undefined &&
                footer.paddingBottom === undefined &&
                footer.paddingStart === undefined &&
                footer.paddingEnd === undefined && {
                  paddingAll: '10px',
                })}
            />
          </div>
        )}
      </div>
    );
  },
);

LfBubble.displayName = 'LfBubble';

export { LfBubble };

import * as React from 'react';

import { cn } from '@/lib/utils';

import {
  getMarginClass,
  getMarginStyle,
  getOffsetStyles,
  getFlexClass,
  getFlexStyle,
  handleAction,
} from './utils/lf-helpers';
import type { FlexButton, FlexAction } from './utils/lf-types';
import { positionVariants, gravityVariants, buttonStyleVariants } from './utils/lf-variants';

export type LfButtonProps = FlexButton & {
  className?: string;
  onAction?: (action: FlexAction) => void;
};

const LfButton = React.forwardRef<HTMLDivElement, LfButtonProps>(
  (
    {
      action,
      flex,
      margin,
      position,
      offsetTop,
      offsetBottom,
      offsetStart,
      offsetEnd,
      height = 'md',
      style = 'link',
      color,
      gravity,
      onAction,
      className,
    },
    ref,
  ) => {
    const marginClass = getMarginClass(margin);
    const marginStyle = getMarginStyle(margin);
    const offsetStyles = getOffsetStyles(offsetTop, offsetBottom, offsetStart, offsetEnd);
    const flexClass = getFlexClass(flex);
    const flexStyle = getFlexStyle(flex);

    const containerStyle: React.CSSProperties = {
      ...marginStyle,
      ...offsetStyles,
      ...flexStyle,
    };

    const buttonStyle: React.CSSProperties = {
      ...(color && (style === 'link' ? { color } : { backgroundColor: color })),
    };

    const clickHandler = handleAction(action, onAction);

    return (
      <div
        ref={ref}
        className={cn(
          'flex flex-col relative min-w-0 max-w-full',
          // Default to flex-1 when flex is not specified (matching .MdBtn behavior)
          flex === undefined ? 'flex-1' : flexClass,
          marginClass,
          position && positionVariants({ position }),
          gravity && gravityVariants({ gravity }),
          className,
        )}
        style={containerStyle}
      >
        {action?.type === 'uri' ? (
          <a
            href={action.uri}
            target="_blank"
            rel="noopener noreferrer"
            className={cn(buttonStyleVariants({ buttonStyle: style, height }), 'w-full')}
            style={buttonStyle}
            onClick={clickHandler}
          >
            <div className="max-w-full overflow-hidden text-ellipsis whitespace-nowrap">{action.label || 'Button'}</div>
          </a>
        ) : (
          <button
            type="button"
            className={cn(buttonStyleVariants({ buttonStyle: style, height }), 'w-full')}
            style={buttonStyle}
            onClick={clickHandler}
          >
            <div className="max-w-full overflow-hidden text-ellipsis whitespace-nowrap">{action.label || 'Button'}</div>
          </button>
        )}
      </div>
    );
  },
);

LfButton.displayName = 'LfButton';

export { LfButton };

import * as React from 'react';

import { cn } from '@/lib/utils';

import { LfBubble } from './lf-bubble';
import type { FlexCarousel, FlexAction } from './utils/lf-types';

export interface LfCarouselProps extends FlexCarousel {
  className?: string;
  onAction?: (action: FlexAction) => void;
}

const LfCarousel = React.forwardRef<HTMLDivElement, LfCarouselProps>(({ contents, onAction, className }, ref) => {
  return (
    <div
      ref={ref}
      className={cn('scrollbar-hide overflow-x-auto overflow-y-hidden', '-webkit-overflow-scrolling-touch', 'w-full', className)}
    >
      <div className="flex w-full gap-[9px] pl-[7px] after:block after:h-px after:w-[7px] after:flex-none after:content-['']">
        {contents.map((bubble, index) => (
          <LfBubble key={index} {...bubble} onAction={onAction} className="w-[80%] flex-none" />
        ))}
      </div>
    </div>
  );
});

LfCarousel.displayName = 'LfCarousel';

export { LfCarousel };

import * as React from 'react';

import { cn } from '@/lib/utils';

import { LfBubble } from './lf-bubble';
import { LfCarousel } from './lf-carousel';
import type { FlexBubble, FlexCarousel, FlexAction } from './utils/lf-types';

export interface LfContainerProps {
  /** The Flex Message content (Bubble or Carousel) */
  content: FlexBubble | FlexCarousel;
  /** Optional callback for handling actions */
  onAction?: (action: FlexAction) => void;
  /** Additional class names */
  className?: string;
}

/**
 * LfContainer component for rendering LINE Flex Messages
 *
 * @example
 * ```tsx
 * <LfContainer
 *   content={flexMessage.contents}
 *   onAction={(action) => console.log(action)}
 * />
 * ```
 */
const LfContainer = React.forwardRef<HTMLDivElement, LfContainerProps>(({ content, onAction, className }, ref) => {
  return (
    <div ref={ref} className={cn('w-full', className)}>
      {content.type === 'bubble' ? (
        <LfBubble {...content} onAction={onAction} />
      ) : (
        <LfCarousel {...content} onAction={onAction} />
      )}
    </div>
  );
});

LfContainer.displayName = 'LfContainer';

export { LfContainer };

import * as React from 'react';

import { cn } from '@/lib/utils';

import { getFlexClass, getFlexStyle } from './utils/lf-helpers';
import type { FlexFiller } from './utils/lf-types';

export interface LfFillerProps extends FlexFiller {
  className?: string;
}

const LfFiller = React.forwardRef<HTMLDivElement, LfFillerProps>(({ flex = 1, className }, ref) => {
  const flexClass = getFlexClass(flex);
  const flexStyle = getFlexStyle(flex);

  return <div ref={ref} className={cn('min-h-0', flexClass, className)} style={flexStyle} />;
});

LfFiller.displayName = 'LfFiller';

export { LfFiller };

import * as React from 'react';

import { cn } from '@/lib/utils';

import { getSizeClass, getSizeStyle, getMarginClass, getMarginStyle, getOffsetStyles } from './utils/lf-helpers';
import type { FlexIcon } from './utils/lf-types';
import { positionVariants } from './utils/lf-variants';

export type LfIconProps = FlexIcon & {
  className?: string;
};

const LfIcon = React.forwardRef<HTMLDivElement, LfIconProps>(
  (
    { url, size = 'md', aspectRatio, margin, position, offsetTop, offsetBottom, offsetStart, offsetEnd, className },
    ref,
  ) => {
    const sizeClass = getSizeClass(size);
    const sizeStyle = getSizeStyle(size);
    const marginClass = getMarginClass(margin);
    const marginStyle = getMarginStyle(margin);
    const offsetStyles = getOffsetStyles(offsetTop, offsetBottom, offsetStart, offsetEnd);

    // Calculate aspect ratio width
    let width = '1em';
    if (aspectRatio) {
      const parts = aspectRatio.split(':').map(Number);
      const [w, h] = parts;
      if (w && h) {
        width = `${w / h}em`;
      }
    }

    const style: React.CSSProperties = {
      ...sizeStyle,
      ...marginStyle,
      ...offsetStyles,
    };

    const iconStyle: React.CSSProperties = {
      backgroundImage: `url('${url}')`,
      width,
      height: '1em',
    };

    return (
      <div
        ref={ref}
        className={cn(
          'relative flex-none',
          sizeClass,
          marginClass,
          position && positionVariants({ position }),
          className,
        )}
        style={style}
      >
        <span
          className="inline-block overflow-hidden bg-contain bg-center bg-no-repeat align-baseline"
          style={iconStyle}
        />
      </div>
    );
  },
);

LfIcon.displayName = 'LfIcon';

export { LfIcon };

import * as React from 'react';

import { cn } from '@/lib/utils';

import {
  getMarginClass,
  getMarginStyle,
  getOffsetStyles,
  getFlexClass,
  getFlexStyle,
  handleAction,
} from './utils/lf-helpers';
import type { FlexImage, FlexAction } from './utils/lf-types';
import { positionVariants, gravityVariants } from './utils/lf-variants';

export type LfImageProps = FlexImage & {
  className?: string;
  onAction?: (action: FlexAction) => void;
};

const LfImage = React.forwardRef<HTMLDivElement, LfImageProps>(
  (
    {
      url,
      flex,
      margin,
      position,
      offsetTop,
      offsetBottom,
      offsetStart,
      offsetEnd,
      align,
      gravity,
      size = 'md',
      aspectRatio,
      aspectMode = 'fit',
      backgroundColor,
      action,
      onAction,
      className,
    },
    ref,
  ) => {
    const marginClass = getMarginClass(margin);
    const marginStyle = getMarginStyle(margin);
    const offsetStyles = getOffsetStyles(offsetTop, offsetBottom, offsetStart, offsetEnd);
    const flexClass = getFlexClass(flex);
    const flexStyle = getFlexStyle(flex);

    const imageSizeMap: Record<string, string> = {
      xxs: 'w-[40px]',
      xs: 'w-[60px]',
      sm: 'w-[80px]',
      md: 'w-[100px]',
      lg: 'w-[120px]',
      xl: 'w-[140px]',
      xxl: 'w-[160px]',
      '3xl': 'w-[180px]',
      '4xl': 'w-[200px]',
      '5xl': 'w-[220px]',
      full: 'w-full',
    };

    // Calculate padding-bottom percentage for aspect ratio (like flex2html.js)
    // Dynamically calculate from aspectRatio string (e.g., '2:3' -> 3*100/2 = 150%)
    const calculateAspectRatio = (ratio: string | undefined): number => {
      if (!ratio) return 100;

      const parts = ratio.split(':');
      if (parts.length === 2 && parts[0] && parts[1]) {
        const width = parseFloat(parts[0]);
        const height = parseFloat(parts[1]);
        if (!isNaN(width) && !isNaN(height) && width > 0) {
          return (height * 100) / width;
        }
      }

      return 100;
    };

    const paddingBottom = calculateAspectRatio(aspectRatio);

    const sizeClass =
      typeof size === 'string' && !size.includes('px') && !size.includes('%') ? imageSizeMap[size] || 'w-[100px]' : '';

    const containerStyle: React.CSSProperties = {
      ...marginStyle,
      ...offsetStyles,
      ...flexStyle,
      ...(typeof size === 'string' && (size.includes('px') || size.includes('%')) && { width: size }),
    };

    const linkStyle: React.CSSProperties = {
      paddingBottom: `${paddingBottom}%`,
    };

    const imageStyle: React.CSSProperties = {
      backgroundImage: `url('${url}')`,
      backgroundSize: aspectMode === 'cover' ? 'cover' : 'contain',
      ...(backgroundColor && { backgroundColor }),
    };

    const clickHandler = handleAction(action, onAction);

    const alignClass = align === 'start' ? 'items-start' : align === 'end' ? 'items-end' : 'items-center';
    const gravityClass = gravity && gravityVariants({ gravity });

    return (
      <div
        ref={ref}
        className={cn(
          'relative flex min-w-0 max-w-full flex-col items-center overflow-hidden',
          // Default to flex-1 when flex is not specified (matching .MdImg behavior)
          flex === undefined ? 'flex-1' : flexClass,
          marginClass,
          position && positionVariants({ position }),
          alignClass,
          gravityClass,
          className,
        )}
        style={containerStyle}
      >
        <div className={cn('max-w-full', sizeClass)}>
          {action?.type === 'uri' ? (
            <a
              href={action.uri}
              target="_blank"
              rel="noopener noreferrer"
              className="relative block w-full"
              style={linkStyle}
              onClick={clickHandler}
            >
              <span
                className="absolute inset-0 top-0 right-0 bottom-0 left-0 block overflow-hidden bg-center bg-no-repeat"
                style={imageStyle}
              />
            </a>
          ) : (
            <div
              className={cn('relative block w-full', action && 'cursor-pointer')}
              style={linkStyle}
              onClick={clickHandler}
            >
              <span
                className="absolute inset-0 top-0 right-0 bottom-0 left-0 block overflow-hidden bg-center bg-no-repeat"
                style={imageStyle}
              />
            </div>
          )}
        </div>
      </div>
    );
  },
);

LfImage.displayName = 'LfImage';

export { LfImage };

export { LfContainer } from './lf-container';
export { LfMessage } from './lf-message';
export { LfBubble } from './lf-bubble';
export { LfCarousel } from './lf-carousel';
export { LfBox, renderLfFlexComponent } from './lf-box';
export { LfButton } from './lf-button';
export { LfImage } from './lf-image';
export { LfVideo } from './lf-video';
export { LfIcon } from './lf-icon';
export { LfText } from './lf-text';
export { LfSpan } from './lf-span';
export { LfSeparator } from './lf-separator';
export { LfFiller } from './lf-filler';
export { LfSpacer } from './lf-spacer';

export type { LfContainerProps } from './lf-container';
export type { LfMessageProps } from './lf-message';
export type { LfBubbleProps } from './lf-bubble';
export type { LfCarouselProps } from './lf-carousel';
export type { LfBoxProps } from './lf-box';
export type { LfButtonProps } from './lf-button';
export type { LfImageProps } from './lf-image';
export type { LfVideoProps } from './lf-video';
export type { LfIconProps } from './lf-icon';
export type { LfTextProps } from './lf-text';
export type { LfSpanProps } from './lf-span';
export type { LfSeparatorProps } from './lf-separator';
export type { LfFillerProps } from './lf-filler';
export type { LfSpacerProps } from './lf-spacer';

import * as React from 'react';

import { cn } from '@/lib/utils';

import { getMarginClass } from './utils/lf-helpers';
import type { FlexSeparator } from './utils/lf-types';

export interface LfSeparatorProps extends FlexSeparator {
  className?: string;
  layout?: 'horizontal' | 'vertical' | 'baseline';
}

const LfSeparator = React.forwardRef<HTMLDivElement, LfSeparatorProps>(
  ({ margin, color, className, layout = 'vertical' }, ref) => {
    const isHorizontal = layout === 'horizontal' || layout === 'baseline';

    // Separator uses margin-top for vertical layout, margin-left for horizontal
    const marginClass = getMarginClass(margin);
    const marginStyle: React.CSSProperties =
      margin && (margin.includes('px') || margin.includes('%'))
        ? isHorizontal
          ? { marginLeft: margin }
          : { marginTop: margin }
        : {};

    const style: React.CSSProperties = {
      ...marginStyle,
      ...(color && { borderColor: color }),
    };

    return (
      <div
        ref={ref}
        className={cn(
          'flex-none',
          !isHorizontal ? 'h-px w-full border-t border-[#d4d6da]' : 'h-full w-px border-l border-[#d4d6da]',
          marginClass,
          className,
        )}
        style={style}
      />
    );
  },
);

